import React, { useState } from 'react';

export const useForm = (callback, intialstate = {}) => {
  const [values, setValues] = useState(intialstate);
  const onChange = e => setvalues({...values, [e.target.name]: e.target.value});
  const onSubmit = e => {
    e.preventDefault();
    callback();
  }

  return {
    onChange,
    onSubmit,
    values
  }
}
