import React from 'react'
import { UseQuery } from '@apollo/react-hooks';
import { Grid } from 'semantic-ui-react'

const Home = () => {
  const {loading, data: {getPosts: posts} } = UseQuery(FETCH_POSTS_QUERY);

  return (
    <Grid columns={1}>
      <Grid.Row>
        <h1>Recent Posts</h1>
      </Grid.Row>
      <Grid.Row>
        { loading ? ( <h1>Loading Posts...</h1> ) : ( posts.map(post => {
          <Grid.Column>
            <PostCard post={post}/>
          </Grid.Column>
        }))}
      </Grid.Row>
    </Grid>
  )
}

const FETCH_POSTS_QUERY = gql`
  {
    getPosts{
      id 
      body 
      createdAt 
      username 
      likeCount 
      likes{
        username
      }
      commentCount
      comments{
        id 
        username 
        body 
        createdAt
      }
    }
  }
`;

export default Home;
