import { gql } from '@apollo/client';

export const FETCH_POSTS_QUERY = gql`
  query getPosts{
    getPosts{
      id
      body
      createdAt
      username
      likeCount
      likes {
        username
      }
      commentCount
      comments {
        id
        username
        createdAt
        body
      }
    }
  }
`;

const CREATE_POST_MUTATION = gql`
  mutation createPost($body: String!){
    createPost(body: $body){
      id 
      body 
      createdAt 
      username
      likes {
        id 
        username 
        createdAt
      }
      likeCount
      comments{
        id 
        body 
        username 
        createdAt
      }
      commentCount
    }
  }
`;
