//* Imported Dependencies
import React, { useState, useEffect} from 'react';
import {Link} from 'react-router-dom';
import { Button, Label, Icon, Popup } from 'semantic-ui-react';
import { useMutation} from '@apollo/client';

//* Imported GraphQL
import {LIKE_POST_MUTATION} from '../util/graphQL';

const LikeButton = ({user, post: {id, likeCount, likes}}) => {
  const [liked, setLiked] = useState(false); 
  const [likePost] = useMutation(LIKE_POST_MUTATION, { variables: {postId: id} })

  useEffect(() => {
    if(user && likes.find(like => like.username === user.username)){ setLiked(true) }
    else { setLiked(false) }
  }, [user, likes]);

  const popUp = ( <Popup content="Like Post" inverted trigger={<Icon name='heart' />} />)
  const isLiked = liked ? <Button color='teal'>{popUp}</Button> : <Button color='teal' basic>{popUp}</Button>
  const likeButton = user ? isLiked : <Button as={Link} to="/login" color='teal' basic>{popUp}</Button>;

  return (
    <Button as='div' labelPosition='right' onClick={likePost}>
      {likeButton}
      <Label basic color='teal' pointing='left'>{likeCount}</Label>
    </Button>
  )
}

export default LikeButton;
