//* Imported Dependencies
import React, {useState, useEffect } from 'react';
import { Button, Label, Popup, Icon } from 'semantic-ui-react';
import { Link } from 'react-router-dom';

const CommentButton = ({user, post:{ id, comments, commentCount}}) => {
  const [commented, setCommented] = useState(false); 

  useEffect(() => {
    if(user && comments.find(comment => comment.username === user.username)){ setCommented(true) }
    else { setCommented(false) }
  }, [user, comments]);

  const popUp = ( <Popup content="Comment on Post" trigger={<Icon name='comment'/>} /> );
  const commentedOn = commented ? <Button color='yellow'>{popUp}</Button> : <Button color='yellow' basic>{popUp}</Button>;
  const commentButton = user ? commentedOn : <Button color='yellow' basic>{popUp}</Button>;

  return (
    <Button labelPosition='right' as={Link} to={`/posts/${id}`}>
      {commentButton}
      <Label basic color='yellow' pointing='left'>{commentCount}</Label>
    </Button>
  )
}

