import React from 'react';
import { Form, Button } from 'semantic-ui-react';
import { gql, useMutation } from '@apollo/client';
import { useForm } from '../util/hooks';
import {FETCH_POSTS_QUERY} from '../util/graphQL';

const PostForm = () => {
  const {values, onChange, onSubmit} = useForm(createPostCallBack, { body: ''});

  const [createPost, { error }] = useMutation(CREATE_POST_MUTATION, {
    variables:values,
    update: (proxy, result) => {
      const data = proxy.readQuery({ guery: FETCH_POSTS_QUERY });
      console.log(data);
      proxy.writeQuery({ query: FETCH_POSTS_QUERY, data: {getPosts: data.getPosts.concat(result.data.createPost)}});
      values.body = '';
      //onError: err => {err.graphQLErrors[0].extensions.exception.errors}
    }
  });

  function createPostCallBack(){
    createPost();
  }
  return (
    <Form onSubmit={onSubmit}>
      <h2>Create a post:</h2>
      <Form.Field>
        <Form.Input
          placeholder="What are you thinking?"
          name="body"
          onChange={onChange}
          value={values.body}
          />
        <Button type="submit" color="teal">Submit</Button>
      </Form.Field>
    </Form>
  )
}

const CREATE_POST_MUTATION = gql`
mutation createPost($body: String!){
  createPost(body: $body){
    id body createdAt username
    likes {
      id username createdAt
    }
    likeCount
    comments{
      id body username createdAt
    }
    commentCount
  }
}
`

export default PostForm;
