import React, { useContext } from 'react';
import '../App.css';
import { Card, Label, Icon, Image, Button, Popup} from 'semantic-ui-react';
import { Link } from 'react-router-dom';
import moment from 'moment';
import { AuthContext } from '../context/auth'; 
import LikeButton from './likeButton';
import DeleteButton from './deleteButton';

const PostCard = ({post : { body, createdAt, username, id, likeCount, commentCount, likes}}) => {
  const { user } = useContext(AuthContext);

  return (
    <Card fluid>
      <Card.Content>
        <Image floated='right' size='mini' src='/images/avatar/large/molly.png'/>
        <Card.Header>{username}</Card.Header>
        <Card.Meta as={Link} to={`/posts/${id}`}>{moment(createdAt).fromNow()}</Card.Meta>
        <Card.Description>{body}</Card.Description>
      </Card.Content>
      <Card.Content extra>
        <LikeButton user={user} post={{ id, likes, likeCount}}/>
        <Button labelPosition='right' as={Link} to={`/posts/${id}`}>
          <Button color='yellow' basic >
            <popup context="Comment on Post" trigger={<Icon name='comment'/>} />
          </Button>
          <Label basic color='yellow' pointing='left'>{commentCount}</Label>
        </Button>
        {user && user.username === username  && <DeleteButton postId={id} />}
      </Card.Content>
    </Card>
  )
}

export default PostCard;
