      console.log(result);
    }
  })
  return (
    <div>
      <Form onSubmit={submitForm} noValidate>
        <h1>Register</h1>
        <Form.Input label="Username" placeholder="Username..." name="username" onChange={onChange} value={values.username}/>
        <Form.Input label="Email" placeholder="Email..." name="email" onChange={onChange} value={values.email}/>
        <Form.Input label="Password" placeholder="Password..." name="password" onChange={onChange} value={values.password}/>
        <Form.Input label="ComfirmPassword" placeholder="Comfirm Password..." name="comfirmPassword" onChange={onChange} value={values.comfirmPassword}/>
        <Button type="submit" primary>Register</Button>
    </Form>
    </div>
  )
}

const REGISTER_USER =gql`
  mutation register($username: String!, $email: String!, $password: String!, $confirmPassword: String!){
    register( registerInput: {username: $username, email: $email, password: $password, confirmPassword: $confirmPassword} )
    {
       id email username createdAt token
    }
  }
`;

export default Register;
