//* Imported Dependencies
import React, { useState, useContext } from 'react';
import { Form, Button } from 'semantic-ui-react';
import { useMutation } from '@apollo/client';

//* Imported Components
import { useForm } from '../util/hooks';
import { AuthContext } from '../context/auth';

//* Imported CSS
import '../App.css';

//* Imported GraphQL
import {REGISTER_USER} from '../util/graphQL'

const Register = props => {
  const context = useContext(AuthContext);
  const [errors, setErrors] = useState({})
  const {values, onChange, onSubmit} = useForm(registerForm, {
    username: '',
    email: '',
    password: '',
    confirmPassword: '',
    profileImg: ''
  })

  const [addUser, { loading }] = useMutation(REGISTER_USER, {
    update: (_, { data: { login: userData}}) => { 
      context.login(userData);
      props.history.push('/');
    },
    onError: err => { setErrors(err.graphQLErrors[0].extensions.exception.errors); },
    variables: values
  })

  function registerForm(){ addUser(); }

  return (
    <div className="form-container">
      <Form onSubmit={onSubmit} noValidate className={loading ? 'loading' : ''}>
        <h1>Register</h1>
        <Form.Input 
          label="Username"
          placeholder="Username..."
          type="text"
          error={errors.username ? true : false}
          name="username"
          onChange={onChange}
          value={values.username}/>
        <Form.Input 
          label="Email" 
          placeholder="Email..." 
          type="email" 
          error={errors.email ? true : false}
          name="email" 
          onChange={onChange} 
          value={values.email}/>
        <Form.Input 
          label="Password" 
          placeholder="Password..." 
          type="password"
          error={errors.password ? true : false} 
          name="password" 
          onChange={onChange} 
          value={values.password}/>
        <Form.Input
          label="Confirm Password" 
          placeholder="Confirm Password..." 
          type="password" 
          error={errors.confirmPassword ? true : false}
          name="confirmPassword" 
          onChange={onChange} 
          value={values.confirmPassword}/>
        <Form.Input
          label="Profile Image"
          placeholder="Select a file..."
          name="profileImg"
          type="file"
          value={values.profileImg}
          error={errors.profileImg ? true : false}
          onChange={onChange}
        />
        <Button type="submit" primary>Register</Button>
    </Form>
    {Object.keys(errors).length > 0 && (
      <div className="ui error message">
        <ul className="list">
          {Object.values(errors).map((value) => (
            <li key={value}>{value}</li>
          ))}
        </ul>
      </div>
    )}
    </div>
  )
}

export default Register;
