import React, { useContext } from 'react';
import '../App.css';
import { Card, Label, Icon, Image , Button} from 'semantic-ui-react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../context/auth'; 

const PostCard = ({post : { body, createdAt, username, id, likeCount, commentCount, likes}}) => {
  const { user } = useContext(AuthContext);

  const likePost = () => {
    console.log('Like Post');
  }

  const commentOnPost = () => {
    console.log('Comment on Post')
  }

  return (
    <Card fluid>
      <Card.Content>
        <Image floated='right' size='mini' src='/images/avatar/large/molly.png'/>
        <Card.Header>{username}</Card.Header>
        <Card.Meta as={Link} to={`/posts/${id}`}>{createdAt}</Card.Meta>
        <Card.Description>{body}</Card.Description>
      </Card.Content>
      <Card.Content extra>
        <Button as='div' labelPosition='right' onClick={likePost}>
          <Button color='teal' basic >
            <Icon name='heart' />
          </Button>
          <Label basic color='teal' pointing='left'>{likeCount}</Label>
        </Button>
        <Button as='div' labelPosition='right' as={Link} to={`/posts/${id}`}>
          <Button color='teal' basic >
            <Icon name='comment'/>
          </Button>
          <Label basic color='teal' pointing='left'>{commentCount}</Label>
        </Button>
        {user && user.username === username  && (
          <Button fluid as="div" color="red" onClick={() => console.log('delete post')}>
            <Icon name="trash alternate outline" />
          </Button>
        )}
      </Card.Content>
    </Card>
  )
}

export default PostCard;
