import React from 'react';
import '../App.css';
import { gql, UseQuery } from '@apollo/client';
import { Grid } from 'semantic-ui-react'
import PostCard from '../components/postCard';

const FETCH_POSTS_QUERY = gql`
  {
    getPosts{ id body createdAt username likeCount commentCount
      likes{ username }
      comments{ id username body createdAt }
    }
  }
`;

const Home = () => {
  let posts ='';
  const {loading, data} = UseQuery(FETCH_POSTS_QUERY);
  if(data){ posts = {data: data.getPosts } }

  return (
    <Grid columns={1}>
      <Grid.Row className='post-title'>
        <h1>Recent Posts</h1>
      </Grid.Row>
      <Grid.Row>
        { loading ? ( <h1>Loading Posts...</h1> ) : ( posts.data && posts.data.map(post => {
          return ( <Grid.Column style={{marginBottom: 20}}>
            <PostCard post={post}/>
          </Grid.Column> )
        }))}
      </Grid.Row>
    </Grid>
  )
}

export default Home;
