import React, { useState } from 'react';
import { Form, Button } from 'semantic-ui-react';
import { gql, useMutation } from '@apollo/client';
import { useForm } from '../util/hooks';
import '../App.css';

const Register = props => {
  const [errors, setErrors] = useState({})
  const {values, onChange, onSubmit} = useForm(registerForm, {
    username: '',
    email: '',
    password: '',
    confirmPassword: ''
  })

  const [addUser, { loading }] = useMutation(REGISTER_USER, {
    update(_, result){ props.history.push('/') },
    onError(err){ setErrors(err.graphQLErrors[0].extensions.exception.errors) },
    variables: values
  })

  function registerForm(){ 
    addUser(); 
  }

  return (
    <div className='form-container'>
      <Form onSubmit={onSubmit} noValidate className={loading ? 'loading' : ''}>
        <h1>Register</h1>
        <Form.Input 
          label="Username"
          placeholder="Username..."
          type="text"
          error={errors.username ? true : false}
          name="username"
          onChange={onChange}
          value={values.username}/>
        <Form.Input 
          label="Email" 
          placeholder="Email..." 
          type="email" 
          error={errors.email ? true : false}
          name="email" 
          onChange={onChange} 
          value={values.email}/>
        <Form.Input 
          label="Password" 
          placeholder="Password..." 
          type="password"
          error={errors.password ? true : false} 
          name="password" 
          onChange={onChange} 
          value={values.password}/>
        <Form.Input l
          abel="ComfirmPassword" 
          placeholder="Comfirm Password..." 
          type="password" 
          error={errors.comfirmPassword ? true : false}
          name="comfirmPassword" 
          onChange={onChange} 
          value={values.comfirmPassword}/>
        <Button type="submit" primary>Register</Button>
    </Form>
    {Object.keys(errors).length > 0 && (
      <div className="ui error message">
        <ul className="list">
          {Object.values(errors).map((value) => (
            <li key={value}>{value}</li>
          ))}
        </ul>
      </div>
    )}
    </div>
  )
}

const REGISTER_USER =gql`
  mutation register($username: String!, $email: String!, $password: String!, $confirmPassword: String!){
    register( registerInput: {username: $username, email: $email, password: $password, confirmPassword: $confirmPassword} )
    {
      id email username createdAt token
    }
  }
`;

export default Register;
